import React, {useEffect,useState} from "react";

import Footer from "../components/Footer";
import HeaderStart from "../components/HeaderStart";
import Menu from "../components/Menu";
import StartNow from "./StarNow";


import SevicioList from "./SevicioList";
import Form from "./Form";

const GestionarServicio = () => {

  const [servicios, setServices] = useState([]);

  const [editedServicio, setEditedService] = useState(null);

  useEffect(()=>{
      fetch('http://localhost:5000/listAllservices',{
          'method':'GET',
          headers : {
          'Content-Type':'application/json'
      }
      })
      .then(response => response.json())
      .then(response => setServices(response))
      .catch(error => console.log(error))
  
  },[])


  const editServicio = (servicio) => {
  
    setEditedService(servicio)

  }

  /*const updateData = (servicio) => {
      const new_servicio = servicios.map(my_servicio => {
        if(my_servicio.id === servicio.id){
          return servicio
        } else {
          return my_servicio
        }
      })*/
      /*setServicio(new_servicio)*/

  



  const openForm = () => {
    setEditedService({title:'', description:''})
  }

  const insertedServicio = (servicio) => {
    const new_servicios = [...servicios, servicio]
    setServicios(new_servicios)
  }


  
  return (
    <>
      <HeaderStart/>
      <Menu/>

      <div className="container">
        <div className = "row">
          <div className = "col">
          <h2> Lista de Servicios</h2>
          </div>
          <div className = "col">
          <button
          className="btn btn-success"
          onClick = {openForm}
          >InsertServicio</button>

          </div>
        </div>  

        

          <SevicioList servicios = {servicios} editServicio = {editServicio} />

          {editedServicio ? <Form servicio = {editedServicio} /*updateData = {updateData} insertedServicio = {insertedServic*//> : null }

      </div>


      <StartNow/>
      <Footer/>
    </>
  
  );
}

export default GestionarServicio;
