import { useState } from "react";


import APIUser from '../views/APIUser';

const Login = () => {
    /*const [state, setState] = useState ({
        username: sessionStorage.getItem('username') ? sessionStorage.getItem('username') : '',
        username: sessionStorage.getItem('password') ? sessionStorage.getItem('password') : '',  
    })*/
    

    const [state, setState] = useState ({
        username : '',
        password : '',
        mensaje : '',
        error : null,
        rememberme : false
    })


   
    const onSubmit = (e) => {
        e.preventDefault ();
        console.log("Enviando formulario");
        let hasError = false
        if(e.target.username.value === "") { 
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        username:{
                            msg : 'Username is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.username;
                return {
                    ...prevState, 
                }
            })
        } 
        if (e.target.password.value === "") {
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        password:{
                            msg : 'Password is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.password;
                return {
                    ...prevState, 
                }
            })
        } 
        
        if (hasError) return;

        getLogin({
            username: e.target.username.value,
            password: e.target.password.value,
            
        })

        username = e.target.username.value,
        password = 

        APIUser.AutenticaUser({username, password})
    }   

    const handleChange = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const handleChangeCheckBox = e => {
        setState({
            ...state,
            [e.target.name]: e.target.checked ? true : false
        })
    }

    const getLogin = data => {
        console.log(data)

        /*fetch("/", {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(resp => resp.json())
            .then(data => {
                console.log("Codigo si no es un usuario valido");
            })*/

            sessionStorage.setItem('username' , data.username);
            sessionStorage.setItem('password' , data.password);
            if(state.rememberme){
                localStorage.setItem('username', data.username)
            }
    }        
    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            >
                            <span aria-hidden="true">
                                <i className="fa fa-close" />
                            </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} onSubmit={onSubmit}>
                                <label>Name :</label>
                                <input
                                    type="text"
                                    name="username"
                                    className="form-control"
                                    placeholder="Enter Username"
                                    value={state.username}
                                    onChange={handleChange}
                                />
                                        
                                {!!state.error && state.error.username ? state.error.username.msg : ""}
                                <label>Passoward :</label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-control"
                                    placeholder="Enter Passoward"
                                    value={state.password}
                                    onChange={handleChange}
                                />
                                {!!state.error && state.error.password ? state.error.password.msg : ""}
                                <label>
                                    <input type="checkbox" name="rememberme" onChange={handleChangeCheckBox} value={true} checked={state.rememberme}  /> Remenber Me
                                </label>
                                <button className="btn btn-default pull-right" type="submit" value="Submit">
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default Login;
