import React from "react";
import { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import APISUser from "../servicio/APIUser";

function Login () {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");


    const history = useHistory();
    useEffect(() => {
        if (localStorage.getItem('user-info')) {
            history.push("/add")
        }

    }, [])

    function login()
    {
        console.warn(email, password)
    }

    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            >
                            <span aria-hidden="true">
                                <i className="fa fa-close" />
                            </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} on={login}>
                                <label>Name :</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="email"
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                                <label>Passoward :</label>
                                <input
                                    type="password"  
                                    className="form-control"
                                    placeholder="password"   
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                <button  className="btn btn-default">
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default Login;
