import React from "react";
import { useState } from "react";

import{ Col, Row, InputGroup, Form, Button } from 'react-bootstrap'

function LoginUser () {

    let user = JSON.parse(localStorage.getItem('user-info'))

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");


    const [fallo, setFallo] = useState(false);
    const [error, setError] = useState("");

    async function ulogin(){

    //const ulogin = () => {
        //console.log("hola estoy en login")
        //console.warn(username, password)
        /*APIUser.AutenticaUser({username, password})
        //.then(response => console.log(JSON.parse(response)))
        .then((response) => JSON.stringify(response))
        .catch(error => console.log(error))

        if (username){
            console.log("llegué aqui")
            return history.push('/gestionar')
        }*/

        if(!username.trim()){
            setFallo(true)
            setError("Por favor, introduzca su username")
            return
        }

        if(!password.trim()){
            setFallo(true)
            setError("Por favor, introduzca su password")
            return
        }

        let item = {username, password}
        let result = await fetch(`http://localhost:5000/login`,{
            method: 'POST',
            headers : {
                'Content-Type':'application/json',
                "Accept" :'application/json' 
                },
            body: JSON.stringify(item) 
            
        });
        setFallo(true)
        result = await result.json();
        localStorage.setItem("user-info",JSON.stringify(result))

        if (user && user.name){
            localStorage.clear();
            window.location.href = '/gestionar';
        }
    }

    const uClose = () => {
        console.log("hola estoy en login y quiero cerrar")
    }

    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                                type="button"
                                className="close"
                                data-dismiss="modal"
                                aria-label="Close"
                                onClick={uClose}
                            >
                                <span aria-hidden="true">
                                    <i className="fa fa-close" />
                                </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            {
                                fallo ? (
                                    <span className="text-danger m-5">{error}</span>
                                ) :
                                (
                                    <span></span>
                                )
                            }
                            <form className="sm-frm" style={{ padding: 25 }} >
                                <label>Name :</label>
                                <input
                                    type="text"
                                    name = "username"
                                    className="form-control"
                                    placeholder="username"
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                                <label>Password :</label>
                                <input
                                    type="password" 
                                    name = "password" 
                                    className="form-control"
                                    placeholder="password"   
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                <button className="btn btn-default" type="button" onClick={ulogin}>
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default LoginUser;
