const getState =({getStore, getActions, setStore})=>{
    return{
        store:{
            nombre:"",
            email:"",
            password:""
        },

        actions: {
            handleInputChange : e => {
                setStore({
                  [e.target.name]: e.target.value
                })
              },

        }

    }
}
export default getState;
