import React, {useState, useEffect} from "react";
import CardService from "./CardService";
import service1 from "../assets/images/service/service1.png";
import service2 from "../assets/images/service/service2.png";
import service3 from "../assets/images/service/service3.png";

const Services = () =>{

    const [servicios, setServices] = useState([]);
        
    /*useEffect(()=>{
        fetch("http://127.0.0.1:5000/services").then(
            res => res.json()
            ).then(
                service => {
                    setServices(service)
                    console.log(service)
                }
            )
    },[])*/

    useEffect(()=>{
        fetch('http://localhost:5000/services',{
            'methods':'GET',
            headers : {
            'Content-Type':'application/json'
        }
        })
        .then(response => response.json())
        .then(response => setServices(response))
        .catch(error => console.log(error))
    
    },[])

    return (
        <>
        <section className="we-do">
            <div className="container">
                <div className="we-do-details">
                    <div className="section-header text-center">
                        <h2>Our Services</h2>
                        <p>
                            Pallamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                        </p>
                        </div>
                        <div className="we-do-carousel">
                        <div className="row">
                                <div className="col-sm-4 col-xs-12">
                                    {props.servicios && props.articles.map(article =>{
                                        return (

                                        <CardService
                                                image={service1}
                                                title={servicios[0].title}
                                                descripcion= {servicios[0].description}
                                        />
                                        )
                                    })}
                                </div>
                                
                        </div>
                    </div>
                </div>
            </div>
        </section>
        </>
    )
}
export default Services;
