import React, { useContext, useEffect, useState } from "react";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormUpdate = ({ location }) => {

    const{actions,store}=useContext(Context); 

    const {
        nombre,
        descripcion,
        autor,  
    }=store; 


    const [proyect, setProyect] = useState([]);

    useEffect(() => {
        actions.fetchOneProyecto(location.state.id); 
        //console.log(pro.map(item => ({ store: item.data })))
        const pro = store.proyectos.map(item => proyecto)
        setProyect(pro)
      }, []);


    

   
    const {handleChange, editar_proyecto} = actions;

    /* const modificarProyecto = (e) => {
        e.preventDefault();
        var id = location.state.id
        console.log(id)
        actions.editar_proyecto(id)
    } */

    
    return (
        <>
            <div className="container">
                <h1>Modificar Proyecto</h1>
                {!!store.proyectos && store.proyectos.map((proyecto) => {

                    return (

                <form autoComplete="off"  onSubmit={() => {editar_proyecto(location.state.id)}} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />

                <button type="submit">Enviar Proyecto</button>
                </form>

                )
            })}
            </div>
        </>
    );
};

export default FormUpdate;
