import React, { useContext, useEffect } from "react";
import { Context } from "../store/appContext";

const VerProyecto = () => {
  const { store, actions } = useContext(Context);

  useEffect(() => {
    actions.fetchProyecto();
  }, []);

  
const {delete_proyecto} = actions
  


  return (
    <> 
      {store.proyectos.map((item, i) => {
        return (
            <div className="container" key={i}> 
              <table>
                
                  <h1>Proyectos Creados</h1>
                  <h2>Nombre del Proyecto:{item.nombre}</h2>
                  <h3>Nombre del Autor:{item.autor}</h3>
                  <h3>Descripcion del Proyecto:{item.descripcion}</h3>
                  <button onClick={(e) => delete_proyecto(e)}>Eliminar</button>
              </table>
            </div>
        )
      })}
    </>
  );
};

export default VerProyecto;


