        /*if (!hasError == false){ 
            console.log("tengo error")

            return;
        }*/
        //if (hasError) return;
    
    
    const {handleChange} = actions;
        
    
    return (  
        <>
            <div className="modal fade bs-example-modal-lg" tabIndex={-1} role="dialog" aria-labelledby="myLargeModalLabel">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                    <div className="modal-header">
                        <button
                        type="button"
                        className="close"
                        data-dismiss="modal"
                        aria-label="Close"
                        >
                        <span aria-hidden="true">
                            <i className="fa fa-close" />
                        </span>
                        </button>
                        <h4 className="modal-title" id="myLargeModalLabel">
                        Registrarse
                        </h4>
                        <form className="lg-frm" autoComplete="off" onSubmit={onSubmit} style={{ padding: 25 }} >
                        <label>Nombre :</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Introduzca su nombre"
                            name="nombre"
                            //value={state.nombre}
                            onChange={handleChange}
                        />
                        {/* {!!state.error && state.error.nombre ? state.error.nombre.msg : ""} */}
                        <br/>

                        <label>Email :</label>
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Introduzca su email"
                            name="email"
                            //value={state.email}
                            onChange={handleChange}
                        />
                        {/* {!!state.error && state.error.email ? state.error.email.msg : ""} */}
                        <br/>

                        <label>Password :</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Introduzca su password"
                            name="password"
                            //value={state.password}
                            onChange={handleChange}
                        />
                        {/* {!!state.error && state.error.password ? state.error.password.msg : ""} */}
                        <br/>
                        
                        <button
                            type="button"
                            className="btn btn-default pull-right"
                            value="Submit"
                        >
                            Enviar
                        </button>
                        </form>
                    </div>
                    </div>
                </div>
            </div>
        </>

    );
    }
   
}

export default Register;
