import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions,store}=useContext(Context); 
    const history = useHistory();
    const {
        nombre ,
        descripcion,
        autor,  
    }=store;

    const {estado, setEstado} = useState(false);
    const {mensaje, setMensaje} = useState("");
    const {valida, setValida} = useState(true);

    /* const[proyectoCreado, setProyectoCreado]= useState(false) */

    const {handleChange, insertarProyecto} = actions;

     // Example starter JavaScript for disabling form submissions if there are invalid fields
     (function () {
        'use strict'
    
        // Fetch all the forms we want to apply custom Bootstrap validation styles to
        var forms = document.querySelectorAll('.needs-validation')
    
        // Loop over them and prevent submission
        Array.prototype.slice.call(forms)
        .forEach(function (form) {
            form.addEventListener('submit', function (event) {
            if (!form.checkValidity()) {
                event.preventDefault()
                event.stopPropagation()
            }
    
            form.classList.add('was-validated')
            }, false)
        })
    })()

    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>

           { estado ? (
                <>
                    <div className="alert alert-success" role="alert">
                    {mensaje}
                    </div>

                    <div className="alert alert-danger" role="alert">
                        {mensaje}
                    </div>
                </>
                )
            : <></>}  
             
                <form autoComplete="off" onSubmit={(e)=>insertarProyecto(e,history)} style={{ padding: 25 }} method="post"> {/* onSubmit={(e)=>sendForm(e,history)} */}

                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}       
                    onChange={handleChange}
                />

                {!nombre ?  
                    <p class="text-danger">Por favor, inserte un nombre de proyecto</p>
                : ""}  
                
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />
                {!descripcion ?  
                    <p class="text-danger">Por favor, inserte una descripción del proyecto</p>
                : ""}

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />
                {!autor ?  
                    <p class="text-danger">Por favor, inserte el autor del proyecto</p>
                : ""}
                <button type="submit">
                        Enviar Proyecto
                    </button>
                </form>
                
            </div>
        </>

        
    );

   
};

export default FormNewProject;
