import React, { useContext, useEffect, useState } from "react";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormUpdate = ({ location }) => {

    const{actions,store}=useContext(Context); 

    const {
        nombre ,
        descripcion,
        autor,  
    }=store;
     
    const {handleChange} = actions;

    useEffect(() => {
        actions.fetchOneProyecto(location.state.id);
      }, []);


    const modificarProyecto = (e) => {
        e.preventDefault();
        var id = location.state.id
        console.log(id)
        actions.editar_proyecto(id)
    }

    
    return (
        <>
            <div className="container">
                <h1>Modificar Proyecto</h1>
                {!!store.proyectos && store.proyectos.map((proyecto) => {
                    return (

                <form autoComplete="off"  onSubmit={modificarProyecto} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={proyecto.nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={proyecto.descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={proyecto.autor}
                    onChange={handleChange}
                />

                <button type="submit">Enviar Proyecto</button>
                </form>

                )
            })}
            </div>
        </>
    );
};

export default FormUpdate;
