import React from "react";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import APISUser from "../views/"

const Login = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const history = useHistory();

    useEffect(() => {
        if(localStorage.getItem('user-info')) {
           //history.push("/add")
           console.log("hola")
        }
    }, [])

    const Login = () => {

        APISUser.UpdateServicio({username, password})
        .then(response => console.log(response))
        .catch(error => console.log(error))

    }

    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            >
                            <span aria-hidden="true">
                                <i className="fa fa-close" />
                            </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} onSubmit={Login}>
                                <label>Name :</label>
                                <input
                                    type="text"
                                    name="username"
                                    className="form-control"
                                    placeholder="Enter Username"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                                        
                                {username ? username.msg : "Error no existe usuario"}
                                <label>Passoward :</label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-control"
                                    placeholder="Enter Passoward"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                {password ? password.msg : "Error no existe pasword"}
                                <label>
                                    <input type="checkbox" name="rememberme" value={true}  /> Remenber Me
                                </label>
                                <button className="btn btn-default pull-right" type="submit" value="Submit">
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default Login;
