import React, { useContext, useState } from "react";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions}=useContext(Context); 

    const [proyectos, setProyectos] = useState ({
        proyecto : '',
        descripcion : '',
        autor : '',  
    })  
     
    const handleChange = e => {
        console.log(e.target.proyecto)
        console.log(e.target.descripcion)
        console.log(e.target.autor)
        
        setProyectos({
            ...proyectos,
            [e.target.name] : e.target.value
        })
    }

    const enviarForm = (e) => {
        e.preventDefault ();
        console.log("Enviando formulario");
        
    }
    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>
                <form autoComplete="off"  onSubmit={enviarForm} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="proyecto"
                    value={proyectos.proyecto}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={proyectos.descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={proyectos.autor}
                    onChange={handleChange}
                />

                <button type="submit">Enviar Proyecto</button>
                </form>
            </div>
        </>
    );
};

export default FormNewProject;
