import React from "react";
import { useEffect, useState } from "react";
import {Redirect} from "react-router-dom";

import APIUser from "../servicio/APIUser";

function LoginUser () {

    //console.log("estoy en LoginUser")

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const [users, setUsers] = useState([]);

    const a = (user) => {
        //console.log(servicio) 
        const new_users = [...users, user]
        setUsers(new_users)
    }


    const ulogin = () => {
        //console.log("hola estoy en login")
        //console.warn(username, password)
        APIUser.AutenticaUser({username, password})
        //.then(response => console.log(response))
        .then(response => response.json())
        .then(response => setUsers(response))
        .catch(error => console.log(error))

        if (users.name){
            return <Redirect to = "/gestionar"/>
        }

    }

    const uClose = () => {
        console.log("hola estoy en login y quiero cerrar")
    }

    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                                type="button"
                                className="close"
                                data-dismiss="modal"
                                aria-label="Close"
                                onClick={uClose}
                            >
                                <span aria-hidden="true">
                                    <i className="fa fa-close" />
                                </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} >
                                <label>Name :</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="username"
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                                <label>Password :</label>
                                <input
                                    type="password"  
                                    className="form-control"
                                    placeholder="password"   
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                <button className="btn btn-default" type="button" onClick={ulogin}>
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default LoginUser;
