import React, { useContext, useEffect, useState } from "react";
import { useHistory } from "react-router";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormUpdate = ({ location }) => {

    const{actions,store}=useContext(Context); 
    const history = useHistory()
    const {
        nombre,
        descripcion,
        autor,  
    }=store; 

    useEffect(() => {
        actions.fetchOneProyecto(location.state.id); 
    }, []);


    set

    const {handleChange} = actions;

    const modificarProyecto = (e) => {
        console.log("hola")
        e.preventDefault();
        var id = location.state.id
        console.log(id)
        actions.editar_proyecto(e,id,history)
    } 

    return (
        <>
            <div className="container">
                <h1>Modificar Proyecto</h1>
                {!!store.proyectos && store.proyectos.map((proyecto) => {

                    return (

                <form autoComplete="off"  onSubmit={modificarProyecto} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />

                <button type="submit">Actualizar</button>
                </form>

                )
            })}
            </div>
        </>
    );
};

export default FormUpdate;
