}

  return (
    <>
    <ul className="d-flex"> 
      <li>
        <h1 className="container">Proyectos Creados</h1> 
        <button className="btn btn-success" disabled={true} >Modificar</button>
        <button className="btn btn-danger" disabled={true} onClick={(e) => delete_proyecto(e)}>Eliminar</button>
      </li>
    </ul>
      <div className="container" >
        <table>
          <tbody>
            <tr>
              <td><strong> </strong></td>
              <td><strong>Nombre del Proyecto</strong></td>
              <td><strong>Nombre del Autor</strong></td>
              <td><strong>Descripcion del Proyecto</strong></td>
            </tr>
              {!!store.proyectos && store.proyectos.map((idproyecto) => {
              return (
                <Fragment key={idproyecto} >
                <tr >
                  <td >
                    <div className="form-check">
                      <input className="form-check-input" onClick={(e) => getItem(e,item.idproyecto)} type="checkbox" value="" id="flexCheckChecked"/>
                    </div>
                  </td>
                  <td>{proyectos.nombre}</td>
                  <td>{proyectos.autor}</td>
                  <td>{proyectos.descripcion}</td>
                  <td>{proyectos.dproyecto}</td> 
                </tr>
                </Fragment>
              ) 
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default VerProyecto;


