import React, {useContext, useState} from "react";
//import Modal from '../components/Modal';
import styled from 'styled-components';
import { Context } from "../store/appContext";

const Consult = () =>{

    const{store, actions}=useContext(Context);
    //const[estadoModal, cambiarEstadoModal] = useState(false);

    return (
        <>
        {/* header-slider-area start */}
        <section className="header-slider-area">
            <div
            id="carousel-example-generic"
            className="carousel slide carousel-fade"
            data-ride="carousel"
            >
            {/* Indicators */}
            <ol className="carousel-indicators">
                <li
                data-target="#carousel-example-generic"
                data-slide-to={0}
                className="active"
                />
                <li data-target="#carousel-example-generic" data-slide-to={1} />
            </ol>
            {/* Wrapper for slides */}
            <div className="carousel-inner" role="listbox">
                <div className="item active">
                <div className="single-slide-item slide-1">
                    <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                        {
                            !!store.profile && (
                                <h1 className="m-3">Perfil de {store?.currentUser?.user?.username}</h1>
                        )
                
            } 
                        <div className="single-slide-item-content">
                            <h2>
                           Consulta tus Proyectos <br /> de negocios con nosotros
                            </h2>
                            <p>

                            Somos la única consulta que apoya tu negocio
                            y que está lista para tomar el desafío de darle publicidad a tus resultados.
                            
                            </p>
                            {/* <button type="button" onClick={() => cambiarEstadoModal(!estadoModal) } className="slide-btn"> 
                            get started
                            </button>*/}
                            {/* <Modal
                                estado={estadoModal}
                                cambiarEstado={cambiarEstadoModal}
                            >
                                <Contenido>
                                    <h1>Ventana Modal</h1>
                                    <p>Reutilizable con opcion de personalizacion</p>
                                    <button className="btn btn-primary">Aceptar</button>
                                </Contenido>
                            </Modal> */}
                        </div>
                        {/* /.single-slide-item-content*/}
                        </div>
                        {/* /.col*/}
                    </div>
                    {/* /.row*/}
                    </div>
                    {/* /.container*/}
                </div>
                {/* /.single-slide-item*/}
                </div>
                {/* /.item .active*/}
                <div className="item">
                <div className="single-slide-item slide-2">
                    <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                        <div className="single-slide-item-content">
                            <h2>
                            Consult Your <br /> Business
                            </h2>
                            <p>
                            We are the unique Consultancy Farm for your business
                            solution, That is ready to take challenge and knockout
                            your business problems.
                            </p>
                            <button type="button" className="slide-btn">
                            get started
                            </button>
                            <button
                            type="button"
                            className="slide-btn
                                                    "
                            >
                            explore more
                            </button>
                        </div>
                        {/* /.single-slide-item-content*/}
                        </div>
                        {/* /.col*/}
                    </div>
                    {/* /.row*/}
                    </div>
                    {/* /.container*/}
                </div>
                {/* /.single-slide-item*/}
                </div>
                {/* /.item .active*/}
            </div>
            {/* /.carousel-inner*/}
            {/* Controls */}
            <a
                className="left carousel-control"
                href="#carousel-example-generic"
                role="button"
                data-slide="prev"
            >
                <span className="lnr lnr-chevron-left" />
            </a>
            <a
                className="right carousel-control"
                href="#carousel-example-generic"
                role="button"
                data-slide="next"
            >
                <span className="lnr lnr-chevron-right" />
            </a>
            </div>
            {/* /.carousel*/}
        </section>
        {/* /.header-slider-area*/}
        {/* header-slider-area end */}
        </>

    )
}
export default Consult; 

const Contenido = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	h1 {
		font-size: 42px;
		font-weight: 700;
		margin-bottom: 10px;
	}
	p {
		font-size: 18px;
		margin-bottom: 20px;
	}
	
`;
