import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions,store}=useContext(Context); 
    const history = useHistory();
    const {
        nombre ,
        descripcion,
        autor,  
    }=store;

    const {estado, setEstado} = useState(false);
    const {mensaje, setMensaje} = useState("");

    /* const[proyectoCreado, setProyectoCreado]= useState(false) */


    const {handleChange, sendForm} = actions;

    /* const mostrarAlerta = () =>{
        setProyectoCreado(!proyectoCreado);
    }; */
    
    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>

            { !estado ? (
                <>
                    <div className="alert alert-success" role="alert">
                    {mensaje}
                    </div>

                    <div className="alert alert-danger" role="alert">
                        {mensaje}
                    </div>
                </>
                )
            : <></>}    
             
                <form autoComplete="off"  onSubmit={(e)=>sendForm(e,history)} style={{ padding: 25 }} method="post"> {/* onSubmit={(e)=>sendForm(e,history)} */}
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />
                <button type="submit">
                        Enviar Proyecto
                    </button>
                </form>
                
            </div>
        </>
    );
};

export default FormNewProject;
