                return {
                    ...prevState, 
                }
            })
        } 
        if(e.target.email.value === "") { 
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        email:{
                            msg : 'Por favor, introduzca su email',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.nombre;
                return {
                    ...prevState, 
