                            type="button"
                            className="btn btn-default pull-right"
                            type="submit"
                            value="Submit"
                            //onClick={registrarse}
                        >
                            Enviar
                        </button>
                        </form>
                    </div>
                    </div>
                </div>
            </div>
        </>

    );
    
   
}

export default Register;
