import React, { useContext, useEffect, useState } from "react";
import { useHistory } from "react-router";
import Modal from "../components/Modal";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Context } from "../store/appContext";

export const Registro = () => {
  const { actions, store } = useContext(Context);

  const{name, emailuser, password}=store
  const history = useHistory();

  //const { modal } = store;

  const { handleChange, register } = actions;
  

  return (
    <>
      <div className="container" >
        <form autoComplete="off" onSubmit={(e)=>register(e, history)} method="post">
          <label>Nombre del Usuario:</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={name}
            onChange={handleChange}
          />
          <>
           {!name.trim() ? (
            <p className="text-danger">
              Por favor, inserte su nombre
            </p>
          ) : (
            ""
          )} 
          </>
          <label>Email del Usuario:</label>
          <input
            type="email"
            className="form-control"
            name="emailuser"
            value={emailuser}
            onChange={handleChange}
          />
          <>
          {!emailuser.trim() ? (
            <p className="text-danger">
              Por favor, inserte su email
            </p>
          ) : (
            ""
          )} 
          </>
          <div className="mb-3">
            <label htmlFor="exampleInputPassword1" className="form-label">
              Password
            </label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={password}
              onChange={handleChange}
            />
            <>
            {!password.trim() ? (
            <p className="text-danger">
              Por favor, inserte su contrase√±a
            </p>
          ) : (
            ""
          )} 
          </>
          </div>
          <button type="submit" className="btn btn-success ">
            Submit
          </button>
        </form>
      </div>
    </>
  );
};



