import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions}=useContext(Context); 
    
     
    const handleInput = e => {
        setState({
            ...state,
            [e.target.name] : e.target.value
        })
    }
    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>
                <form autoComplete="off" onSubmit={enviarDatos} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    onChange={handleInput}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="nombre"
                    onChange={handleInput} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="nombre"
                    onChange={handleInput}
                />

                <button type="button">Enviar Proyecto</button>
                </form>
            </div>
        </>
    );
};

export default FormNewProject;
