import React, { useState } from 'react';
import APIService from './APIService';


function Form(props) {

    console.log(props);

    const [title, setTitle] = useState(props.servicio.title)
    const [description, setDescripción] = useState(props.servicio.description)

    const updateServicio = () => {

        APIService.updateServicio(props.servicio.id, {title, description})
        .then(response => console.log(response))
        .catch(error => console.log(error))

    }

    return (
        <div>
            {props.servicio ? (

                <div className = "mb-3">

                    <label htmlFor = "title" className ="form-label"> Titulo</label>

                    <input type = "text" className ="form-control" 

                    value = {title}
                    
                    placeholder = "Por favor coloque el titulo"

                    onChange = {(e) => setTitle(e.target.value)}
                    
                    />
                    
                    <label htmlFor = "description" className ="form-label"> Descripcion</label>

                    <textarea
                    rows = "5"
                    value = {description}
                    className ="form-control" 
                    
                    placeholder = "Por favor coloque una descripcion"
                    onChange = {(e) => setDescripción(e.target.value)}
                    
                    />

                    <button
                    onClick = {updateServicio}
                    className = "btn btn-success mt-3"
                    > Actualizar</button>

                </div> 

            ):null}

        </div>
    )
}

export default Form
