const getState =({getStore, getActions, setStore})=>{
    return{
        store:{
            nombre:"",
            email:"",
            password:""
        },

        actions: {
            handleChange : e => {
                console.log(setStore.name)
                setStore({
                  [e.target.name]: e.target.value
                })
            },

        }

    }
};

