            [e.target.name] : e.target.value
        })
    }
        
    const enviarDatos = (e) => {
        e.preventDefault ();
        let hasError = false
        if(e.target.name.value === "") { 
            setDataUser(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        name:{
                            msg : "Por favor, introduzca su nombre",
                        } 
                    }
                }    
            })
            hasError = true;
