import Modal from '../components/Modal';
import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions,store}=useContext(Context); 
    const history = useHistory();
    const {
        nombre ,
        descripcion,
        autor,
        modal,  
    }=store;

    const[estadoModal, cambiarEstadoModal] = useState(false);    

    const {handleChange, insertarProyecto} = actions;

     // Example starter JavaScript for disabling form submissions if there are invalid fields
     (function () {
        'use strict'
    
        // Fetch all the forms we want to apply custom Bootstrap validation styles to
        var forms = document.querySelectorAll('.needs-validation')
    
        // Loop over them and prevent submission
        Array.prototype.slice.call(forms)
        .forEach(function (form) {
            form.addEventListener('submit', function (event) {
            if (!form.checkValidity()) {
                event.preventDefault()
                event.stopPropagation()
            }
    
            form.classList.add('was-validated')
            }, false)
        })
    })()

    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>
             
                <form autoComplete="off" onSubmit={(e)=>insertarProyecto(e,history)} style={{ padding: 25 }} method="post"> 

                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}       
                    onChange={handleChange}
                />

                {!nombre ?  
                    <p className="text-danger">Por favor, inserte un nombre de proyecto</p>
                : ""}  
                
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />
                {!descripcion ?  
                    <p className="text-danger">Por favor, inserte una descripción del proyecto</p>
                : ""}

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />
                {!autor ?  
                    <p className="text-danger">Por favor, inserte el autor del proyecto</p>
                : ""}

                <button type="submit" disabled = {(nombre && descripcion && autor) ? false:true}>
                    Insertar Proyecto
                </button>
                
                </form>
                
            </div>
              <Modal 
                estado={modal}
                cambiarEstado={cambiarEstadoModal}
                >
                <Contenido>
                    <p>El proyecto {dataForm.storenombre} del autor {autor} se ha insertado satisfactoriamente</p>
                    <button className="btn btn-primary">Aceptar</button>
                </Contenido>
            </Modal>
        </>

        
    );

   
};

export default FormNewProject;

const Contenido = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	h1 {
		font-size: 42px;
		font-weight: 700;
		margin-bottom: 10px;
	}
	p {
		font-size: 18px;
		margin-bottom: 20px;
	}
	
`;
