import { useState } from "react";



const Register = () => {


    const [state, setState] = useState ({
        nombre : '',
        email : '',
        password : '',
        error : null,
        
    })

    const onSubmit = (e) => {
        e.preventDefault ();
        console.log("Enviando formulario");
        let hasError = false
        if(e.target.name.value === "") { 
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        nombre:{
                            msg : 'Username is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.nombre;
                return {
                    ...prevState, 
                }
            })
        } 
        if (e.target.password.value === "") {
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        password:{
                            msg : 'Password is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.password;
                return {
                    ...prevState, 
                }
            })
        } 
        
        if (hasError) return;

        getLogin({
            nombre: e.target.name.value,
            password: e.target.password.value,
        })
    }   

    const handleChange = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }


    const getLogin = data => {
        console.log(data)

        /*fetch("/", {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(resp => resp.json())
            .then(data => {
                console.log("Codigo si no es un usuario valido");
            })*/

            sessionStorage.setItem('nombre' , data.nombre);
            sessionStorage.setItem('password' , data.password);
            if(state.rememberme){
                localStorage.setItem('nombre', data.nombre)
            }
    }        



    
    return (  
        <>
            {/* large modal */}
            <div className="modal fade bs-example-modal-lg" tabIndex={-1} role="dialog" aria-labelledby="myLargeModalLabel">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                    <div className="modal-header">
                        <button
                        type="button"
                        className="close"
                        data-dismiss="modal"
                        aria-label="Close"
                        >
                        <span aria-hidden="true">
                            <i className="fa fa-close" />
                        </span>
                        </button>
                        <h4 className="modal-title" id="myLargeModalLabel">
                        Register
                        </h4>
                        <form className="lg-frm" style={{ padding: 25 }} onSubmit={onSubmit}>
                        <label>Name :</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Enter Name"
                            name="nombre"
                        />
                        <label>Email :</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Enter Email"
                            name="email"
                            value={state.email}
                            onChange={handleChange}
                        />
                        {!!state.error && state.error.nombre ? state.error.nombre.msg : ""}
                        <label>Passoward :</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Enter Passoward"
                            name="password"
                            value={state.password}
                            onChange={handleChange}
                        />
                        {!!state.error && state.error.password ? state.error.password.msg : ""}
                        <button
                            type="button"
                            className="btn btn-default pull-right"
                            type="submit"
                            value="Submit"
                        >
                            Submit
                        </button>
                        </form>
                    </div>
                    </div>
                </div>
            </div>
        </>

    );
}
export default Register;
