import React from "react";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";


import APIUser from '../views/APIUser';

const Login = () => {

    const [username, setName] = useState("");
    const [password, setPassword] = useState("");

    const history = useHistory();

    useEffect(() => {
        if(localStorage.getItem('user-info')) {
            history.push("/add")
        }
    }, [])


    const handleChange = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }


    async function getLogin(){
        console.warn(username,password);
        let item = {username,password};
        let result = await fetch(`http://localhost:5000/login`,{
            method: 'POST',
            headers : {
                'Content-Type':'application/json',
                "Accept" :'application/json' 
                },
            body: JSON.stringify(item)
            
        });
        result = await result.json();
        localStorage.setItem("user-info", JSON.stringify(result))
        history.push("/add")
    }
    
    
    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            >
                            <span aria-hidden="true">
                                <i className="fa fa-close" />
                            </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} onSubmit={Login}>
                                <label>Name :</label>
                                <input
                                    type="text"
                                    name="username"
                                    className="form-control"
                                    placeholder="Enter Username"
                                    value={state.username}
                                    onChange={handleChange}
                                />
                                        
                                {!!state.error && state.error.username ? state.error.username.msg : ""}
                                <label>Passoward :</label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-control"
                                    placeholder="Enter Passoward"
                                    value={state.password}
                                    onChange={handleChange}
                                />
                                {!!state.error && state.error.password ? state.error.password.msg : ""}
                                <label>
                                    <input type="checkbox" name="rememberme" onChange={handleChangeCheckBox} value={true} checked={state.rememberme}  /> Remenber Me
                                </label>
                                <button className="btn btn-default pull-right" type="submit" value="Submit">
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default Login;
