export default class APIService{

    static updateServicio (id, title){
        return fetch(`http://localhost:5000/upservices/${id}`,{
            'method':'PUT',
            headers : {
            'Content-Type':'application/json'
        },
        title:JSON.stringify(title)
        })
        .then(response => response.json())
    }

}
