import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import { Context } from '../store/appContext'
import "../style/form.css";

const FormNewProject = () => {

    const{actions,store}=useContext(Context); 
    const history = useHistory();
    const {
        nombre ,
        descripcion,
        autor,  
    }=store;
     



    const[proyectoCreado, setProyectoCreado]= useState(false)


    const {handleChange, sendForm} = actions;

    const mostrarAlerta = () =>{
        setProyectoCreado(!proyectoCreado);
    };
    
    return (
        <>
            <div className="container">
                <h1>Proyecto Nuevo</h1>
                <form autoComplete="off" onClick={mostrarAlerta} onSubmit={(e)=>sendForm(e,history)} style={{ padding: 25 }} method="post"> {/* onSubmit={(e)=>sendForm(e,history)} */}
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />
                
                </form>
                { proyectoCreado ?  
                    <>
                    <div class="alert alert-success" role="alert">
                        Proyecto crado satisfactoriamente.
                    </div>
                        <Link to="/verproyecto"><button type="button" class="btn btn-outline-primary">Primary</button></Link> 
                    </>    
                :
                <button type="submit">Enviar Proyecto</button>
                }
            </div>
        </>
    );
};

export default FormNewProject;
