    }

    
    return (
        <>
            <div className="container">
                <h1>Modificar Proyecto</h1>
                <form autoComplete="off"  onSubmit={modificarProyecto} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={nombre}
                    onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    value={descripcion}
                    onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    value={autor}
                    onChange={handleChange}
                />

                <button type="submit">Enviar Proyecto</button>
                </form>
            </div>
        </>
    );
};

export default FormUpdate;
