import React, { useContext, useEffect } from "react";
import { Context } from "../store/appContext";

const VerProyecto = () => {
  const { store, actions } = useContext(Context);

  useEffect(() => {
    actions.fetchProyecto();
  }, []);

  
const {delete_proyecto} = actions
  


  return (
    <> 
    <h1 className="container">Proyectos Creados</h1> 
      {store.proyectos.map((item, i) => {
        return (
            <div className="container" key={i}>
              <table>
                <tr>
                  <td><strong>Nombre del Proyecto</strong></td>
                  <td><strong>Nombre del Autor</strong></td>
                  <td><strong>Descripcion del Proyecto</strong></td>
                </tr>
                <tr>
                  <td>{item.nombre}</td>
                  <td>{item.autor}</td>
                  <td>{item.descripcion}</td>
                  <button className="btn btn-danger" onClick={(e) => delete_proyecto(e)}>Eliminar</button>
                </tr>
              </table>
            </div>
        )
      })}
    </>
  );
};

export default VerProyecto;


