import React from "react";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import APISUser from "../views/APIUser";

const Login = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");


    const [state, setState] = useState ({
        username : '',
        password : '',
        mensaje : '',
        error : null,
        rememberme : false
    })


    const onSubmit = (e) => {
        e.preventDefault ();
        console.log("Enviando formulario");
        let hasError = false
        if(e.target.username.value === "") { 
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        username:{
                            msg : 'Username is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.username;
                return {
                    ...prevState, 
                }
            })
        } 
        if (e.target.password.value === "") {
            setState(prevState => {
                return {
                    ...prevState,
                    error: {
                        ...prevState.error,
                        password:{
                            msg : 'Password is required',
                        } 
                    }
                }    
            })
            hasError = true;
        } else {
            setState(prevState => {
                delete prevState.error?.password;
                return {
                    ...prevState, 
                }
            })
        } 
        
        if (hasError) return;

        getLogin({
            username: e.target.username.value,
            password: e.target.password.value,
        })

    }   

    const handleChange = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const handleChangeCheckBox = e => {
        setState({
            ...state,
            [e.target.name]: e.target.checked ? true : false
        })
    }


    const getLogin = data => {
        //console.log(data)
        APISUser.AutenticaUser({username, password})
        .then(response => console.log(response))
        .catch(error => console.log(error))
        
    }

 
    useEffect(() => {
        if(localStorage.getItem('user-info')) {
           //history.push("/add")
           console.log("hola")
        }
    }, [])

    const Login = () => {

        APISUser.UpdateServicio({username, password})
        .then(response => console.log(response))
        .catch(error => console.log(error))

    }

    return ( 
        <>
            {/* small modal */}
            <div className="modal fade bs-example-modal-sm" tabIndex={-1} role="dialog" aria-labelledby="mySmallModalLabel">
                <div className="modal-dialog modal-sm" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            >
                            <span aria-hidden="true">
                                <i className="fa fa-close" />
                            </span>
                            </button>
                            <h4 className="modal-title" id="mySmallModalLabel">
                            Sign In
                            </h4>
                            <form className="sm-frm" style={{ padding: 25 }} onSubmit={onSubmit}>
                                <label>Name :</label>
                                <input
                                    type="text"
                                    name="username"
                                    className="form-control"
                                    placeholder="Enter Username"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                                        
                                {username ? username.msg : "Error no existe usuario"}
                                <label>Passoward :</label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-control"
                                    placeholder="Enter Passoward"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                {password ? password.msg : "Error no existe pasword"}
                                <label>
                                    <input type="checkbox" name="rememberme" value={true}  /> Remenber Me
                                </label>
                                <button className="btn btn-default pull-right" type="submit" value="Submit">
                                    Enviar
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
        );
}

export default Login;
