import React, { Fragment, useContext, useEffect, useState } from "react";
import { Context } from "../store/appContext";
import "../style/form.css";

const VerProyecto = () => {
  const { store, actions } = useContext(Context);

  useEffect(() => {
    actions.fetchProyecto();
  }, []);


 
  const [checkBoxSelected, setCheckBoxSelected]=useState([]);

  const [botonActivo, setBotonActivo] = useState(false);

  const handleChangeCheckBox=(e)=>{

    console.log(e.target.value);
    var auxiliar=null;
    if(checkBoxSelected.includes(e.target.value)){
      auxiliar=checkBoxSelected.filter(elemento=>elemento!==e.target.value);

    }else{
      auxiliar=checkBoxSelected.concat(e.target.value);
    }
    setCheckBoxSelected(auxiliar);

    if(auxiliar.length>0){
      setBotonActivo(true);

    }else{
      setBotonActivo(false)
    }
    console.log(auxiliar);
    let x = parseInt(auxiliar);
    console.log(x);
  }
  
  const{editar}=actions;

  return (
    <>
    <div className="container padre"> 
        <h1 >Proyectos Creados</h1> 
        <button className="btn hijo btn-success" onClick={editar} disabled={!botonActivo} >Modificar</button>
        <button className="btn hijo btn-danger" disabled={!botonActivo} >Eliminar</button>
    </div>
      <div className="container" >
        <table>
          <tbody>
            <tr>
              <td><strong> </strong></td>
              <td><h4><strong>Nombre del Proyecto</strong></h4></td>
              <td><h4><strong>Nombre del Autor</strong></h4></td>
              <td><h4><strong>Descripcion del Proyecto</strong></h4></td>
              <td><h4><strong>Id</strong></h4></td>
            </tr>
              {!!store.proyectos && store.proyectos.map((proyecto) => {
              return (
                <Fragment key={proyecto.idproyecto} >
                <tr >
                  <td >
                    <div className="form-check">
                      <input className="form-check-input"  type="checkbox" value={proyecto.idproyecto}  onChange={handleChangeCheckBox}  id="flexCheckChecked"/>
                    </div>
                  </td>
                  <td><h4>{proyecto.nombre}</h4></td>
                  <td><h4>{proyecto.autor}</h4></td>
                  <td><h4>{proyecto.descripcion}</h4></td>
                  <td><h4>{proyecto.idproyecto}</h4></td> 
                </tr>
                </Fragment>
              ) 
            })}
          </tbody>
        </table>
        <div className="container">
                <h1>Proyecto Nuevo</h1>
                <form autoComplete="off"  onSubmit={sendForm} style={{ padding: 25 }} method="post">
                <label>
                    Nombre del Proyecto:
                </label>
                <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    //value={nombre}
                    //onChange={handleChange}
                />
                <label>
                    Descripcion del Proyecto:
                </label>
                <textarea
                    className="form-control" 
                    name="descripcion"
                    //value={descripcion}
                    //onChange={handleChange} 
                />

                <label>
                    Autor:
                </label>
                <input type="text"
                    className="form-control" 
                    name="autor"
                    //value={autor}
                    //onChange={handleChange}
                />

                <button type="submit">Enviar Proyecto</button>
                </form>
            </div>
      </div>
    </>
  );
};

export default VerProyecto;
